mapper.delete:
delete from u_role_x_resource
where roleId=#{roleId}
and resId in

    #{res.id}

delete from u_user_x_resource where userId=#{userId}
and resId in

    #{res.id}

mapper.select:
select a.id,name,displayText,groups as `group`,smallGroup,parentId,orderNum,resType as resourceType,elementId
from u_resource a
inner join u_role_x_resource b on a.id=b.resId
where b.roleId in

    #{role.id}

and a.groups=#{group}

select a.id,name,displayText,groups as `group`,smallGroup,parentId,orderNum,resType as resourceType,elementId
from u_resource a
inner join u_user_x_resource b on a.id=b.resId
where b.userId=#{userId}
and a.groups=#{group}
