mapper.update:
update u_user a
set a.realname=#{user.relaName},a.phone=#{user.phone},a.email=#{user.email}
where a.id=#{user.id}

mapper.select:
select id,username,phone,email,pwd as password,realname
from u_user a
where a.username=#{userName}

select id,username,phone,email,pwd as password,realname
from u_user a
where a.username=#{phone}

select id,username,phone,email,pwd as password,realname
from u_user a
where a.username=#{email}
